#include <bits/stdc++.h>
using namespace std;

// sort tạo mảng tăng dần, nếu bộ 3 cạnh lớn nhất không tạo thành tam giác
// thì các các cạnh nhỏ hơn cũng tương tự
// độ phức tạp O(nlog(n)) => do sắp xếp
class Solution {
public:
    int largestPerimeter(vector<int>& nums) {
        sort(nums.begin(), nums.end());
        for (int i=nums.size()-1;i>=2;i--) {
            if (nums[i] < nums[i-1] + nums[i-2]) {
                return nums[i]+nums[i-1]+nums[i-2];
            }
        }
        return 0;
    }
};

